package com.example.userssp

import android.content.Context
import android.content.DialogInterface
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.userssp.databinding.ActivityMainBinding
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.textfield.TextInputEditText

class MainActivity : AppCompatActivity(),  OnClickListener {

    private lateinit var userAdapter: UserAdapter
    private lateinit var  linearLayoutManager: RecyclerView.LayoutManager
    private lateinit var binding : ActivityMainBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
//        setContentView(R.layout.activity_main)

        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

        //preferencias
        val preferences = getPreferences(Context.MODE_PRIVATE)
        //comprueba si existe como true
        val isFirstTime = preferences.getBoolean(getString(R.string.sp_first_time), true)
        var isInvitado = preferences.getBoolean(getString(R.string.sp_invitado), true)
        //Se edita como false
        Toast.makeText(this, "Modo invitado Â´{$isInvitado}", Toast.LENGTH_SHORT).show()
        if (isFirstTime){
            val dialoView = layoutInflater.inflate(R.layout.dialog_register,null)
            MaterialAlertDialogBuilder(this)
                    .setTitle(R.string.dialog_confirm)
                    .setView(dialoView)
                    .setCancelable(false)
                    .setNeutralButton(R.string.btInvitado,DialogInterface.OnClickListener { dialogInterface, i ->
                        preferences.edit().putBoolean(getString(R.string.sp_invitado), false).apply()
                    })
                    .setPositiveButton(R.string.btregistro, DialogInterface.OnClickListener { dialogInterface, i ->
                        val username = dialoView.findViewById<TextInputEditText>(R.id.etUsername).text.toString()
                        preferences.edit().putBoolean(getString(R.string.sp_first_time), false).commit()
                        with(preferences.edit()) {
                            putBoolean(getString(R.string.sp_first_time), false)
                            putString(getString(R.string.sp_username), username)
                                    .apply()
                        }
                        Toast.makeText(this, "correccto", Toast.LENGTH_SHORT).show()
                    })
                    .show()

        }

        //
        userAdapter = UserAdapter(getUsers(), this)
        linearLayoutManager = LinearLayoutManager(this)

        binding.recyclerView.apply {
            layoutManager = linearLayoutManager
            adapter = userAdapter

        }

    }
    private fun getUsers(): MutableList<User>{
        val users = mutableListOf<User>()

        val alain = User(1,"Alain","Nicolas","data:image/jpeg;base64,")
        val carmen = User(2,"carmen","perico","data:image/jpeg;base64,")

        users.add(alain)
        users.add(carmen)

        users.add(alain)
        users.add(carmen)


        return users
    }

    override fun onClick(user: User) {

        Toast.makeText(this,user.getFullName(),Toast.LENGTH_LONG).show()
    }

}